import axios from "axios";
const headers = {
    "Content-Type": "application/json",
  };
const url_ = "http://localhost:8800";

export const Api = {
  signup: (email, password, login) => {
      return axios.post(url_ + "/user", {
          email: email,
          password: password,
          login: login
      });
  },
  login: (email, password) => {
      return axios.post(url_ + "/user", {
          password: password,
          email: email,
      });
  },
}
